<template>
  <div v-if="visible" tabindex="2" ref="main" @keydown.enter.exact="hide" class="shadow">
    <div class="eidt-layer">
      <h1>Tree Manage</h1>
      <div>
        <span>class:</span>
        <input type="text" v-model="classStr" />
      </div>
      <div>
        <button @click="success">完成</button>
      </div>
    </div>
  </div>
</template>

<script>
export default {
  name: "cl-tree",
  data() {
    return {
      classStr: "",
      visible: false
    };
  },
  methods: {
    show() {
      this.visible = true;
      this.$nextTick(() => {
        this.$refs.main.focus();
      });
    },
    hide() {
      this.visible = false;
      this.$emit("close");
    },
    success() {
      this.hide();
    }
  }
};
</script>
